def max_chain_length(arr, n): 
    max_len = 0
    mcl = [1] * n 
    arr.sort(key = lambda i: i.a) 
    for i in range(1, n): 
        for j in range(0, i): 
            if (arr[i].a > arr[j].b and
                mcl[i] < mcl[j] + 1): 
                mcl[i] = mcl[j] + 1
    for i in range(n): 
        if (max_len < mcl[i]): 
            max_len = mcl[i] 
    return max_len

